mapper.insert:

	SELECT NVL(MAX(ATCH_FLE_SN),0)+1			         
	  FROM IBIMS989B 								
	 WHERE FILE_KEY1 = #{fileKey1}				
	   AND FILE_KEY2 = #{fileKey2}


INSERT INTO IBIMS989B								
       ( 
          FILE_KEY1					    
        , FILE_KEY2						
        , ATCH_FLE_SN		
        , SV_FILE_PATH_NM	
        , SV_FILE_NM		
        , SV_FILE_EXPNSN_NM	
        , SV_FILE_SZ		
        , SCRN_MENU_ID		
        , ORG_FILE_NM		
        , RGST_DT			
        , DLT_F				
        , HNDL_DY_TM		
        , HNDL_DPRT_CD		
        , HNDL_P_ENO		
       )
VALUES (
		  #{fileKey1}					
		, NVL(#{fileKey2},'*')					
		, #{atchFleSn}					
		, #{svFilePathNm}				
		, #{svFileNm}					
		, #{svFileExpnsnNm}				
		, #{svFileSz}					
		, #{scrnMenuId}					
		, #{orgFileNm}					
		, TO_DATE(SYSDATE,'YYYYMMDD')	
		, 'N'							
		, CURRENT_TIMESTAMP()			
		, #{hndlDprtCd}					
		, #{hndlPEno}					
       )

mapper.update:
UPDATE IBIMS989B							   
   SET DLT_F = 'Y'
	 , HNDL_DY_TM = SYSDATE			
	 , HNDL_DPRT_CD	= #{hndlDprtCd}				
	 , HNDL_P_ENO = #{hndlPEno}					
 WHERE  FILE_KEY1  = #{fileKey1}		             
    AND FILE_KEY2  = #{fileKey2}		             
    AND ATCH_FLE_SN = #{atchFleSn}
