mapper.select:
SELECT DECD_SN
  FROM IBIMS231B
 WHERE 1=1
   AND DEAL_NO = #{dealNo}

   AND PRDT_CD = #{prdtCd}

   AND DECD_JOB_DCD = (
                         SELECT CD_VL_ID
                           FROM IBIMS002B
                         WHERE 1=1
                           AND CMNS_CD_GRP = 'D005'
                           AND RSLT_CD_VL = #{decdJobDcd}
                         )
   AND SCRN_NO = #{scrnNo}
   AND EXC_SEQ = #{excSeq}
   AND RQST_SQ = #{rqstSq}
   AND TR_SEQ = #{trSeq}

		SELECT NVL(MAX(
              CASE WHEN
                  (
                    SELECT DECODE(COUNT(*), 0, 'Y', DECODE(NVL(SUM(DECODE(DECD_STTS_DCD, '2', 1, 0)), 0), COUNT(*), 'Y', 'N'))
                      FROM IBIMS232B
                     WHERE 1=1
                       AND DECD_SN = T2.DECD_SN
                       AND DECD_SQ < T2.DECD_SQ
                  ) = 'N' THEN '0' ELSE T2.DECD_STTS_DCD END
              ), '0')AS DECD_STTS_DCD
		  FROM IBIMS231B T1
 LEFT JOIN IBIMS232B T2
	 		  ON T1.DECD_SN = T2.DECD_SN
		 WHERE 1=1
		   AND T1.DEAL_NO = #{dealNo}
		   AND T1.PRDT_CD = NVL(#{prdtCd}, ' ')
		   AND T1.DECD_JOB_DCD = (
		   						  SELECT CD_VL_ID
		   						    FROM IBIMS002B
		   						   WHERE 1=1
		   						     AND CMNS_CD_GRP = 'D005'
		   						     AND RSLT_CD_VL = #{decdJobDcd}
		   						  )
		   AND T1.SCRN_NO = #{scrnNo}
		   AND T1.EXC_SEQ = #{excSeq}
		   AND T1.RQST_SQ = #{rqstSq}
		   AND T1.TR_SEQ = #{trSeq}
       AND T2.DCFC_ENO = #{dcfcEno}
       /* 결재자이긴한데 현재 결재순서가 아닌경우 결재요청 버튼도 보여주면 안됨 */

  SELECT NVL(
              (
               SELECT T1.DECD_STTS_DCD
		             FROM IBIMS231B T1
		            WHERE 1=1
		              AND T1.DEAL_NO = #{dealNo}
		              AND T1.PRDT_CD = NVL(#{prdtCd}, ' ')
		              AND T1.DECD_JOB_DCD = (
		              						  SELECT CD_VL_ID
		              						    FROM IBIMS002B
		              						   WHERE 1=1
		              						     AND CMNS_CD_GRP = 'D005'
		              						     AND RSLT_CD_VL = #{decdJobDcd}
		              						  )
		              AND T1.SCRN_NO = #{scrnNo}
		              AND T1.EXC_SEQ = #{excSeq}
		              AND T1.RQST_SQ = #{rqstSq}
		              AND T1.TR_SEQ = #{trSeq}
               ), -1) AS DECD_STTS_DCD
    FROM DUAL

mapper.insert:
INSERT INTO IBIMS231B(
    DECD_SN
  , CHRR_ENO
  , APVL_RQST_P_ENO
  , DECD_STEP_DCD
  , DECD_STTS_DCD
  , DEAL_NO
  , PRDT_CD
  , DECD_JOB_DCD
  , SCRN_NO
  , APVL_RQST_CNTN
  , RQST_DTM
  , RQST_CNCL_DTM
  , PRCS_RSLT_DCD
  , EXC_SEQ
  , RQST_SQ
  , TR_SEQ
  , ERR_CNTN
  , LAST_DECD_SQ
  , HND_DETL_DTM
  , HND_EMPNO
  , HND_TMNL_NO
  , HND_TR_ID
  , GUID
) VALUES (
    #{decdSn}
  , (
    SELECT MAX(EMPNO)
      FROM IBIMS003B
     WHERE 1=1
       AND ATH_CD = (
                      SELECT DPRT_CD || 8
                        FROM IBIMS003B
                       WHERE 1=1
                         AND EMPNO = #{apvlRqstPEno}
                     )
  )
  , #{apvlRqstPEno}
  , #{decdStepDcd}
  , #{decdSttsDcd}
  , NVL(#{dealNo}, '')
  , NVL(#{prdtCd}, ' ')
  , (
     SELECT CD_VL_ID
       FROM IBIMS002B
      WHERE 1=1
        AND CMNS_CD_GRP = 'D005'
        AND RSLT_CD_VL = #{decdJobDcd}
     )
  , #{scrnNo}
  , #{apvlRqstCntn}
  , SYSDATE
  , #{rqstCnclDtm}
  , '00'
  , NVL(#{excSeq}, 0)
  , NVL(#{rqstSq}, 0)
  , NVL(#{trSeq}, 0)
  , #{errCntn}
  , #{lastDecdSq}
  , SYSDATE
  , #{hndEmpno}
  , #{hndTmnlNo}
  , #{hndTrId}
  , #{guid}
)

mapper.update:
UPDATE IBIMS231B
   SET DECD_STEP_DCD = #{decdStepDcd}

     , DECD_STTS_DCD = #{decdSttsDcd}


     , APVL_RQST_CNTN = #{apvlRqstCntn}


     , ERR_CNTN = #{errCntn}


     , PRCS_RSLT_DCD = #{prcsRsltDcd}

     , RQST_CNCL_DTM = CASE 
                       WHEN #{decdSttsDcd} = '4' THEN SYSDATE 
                       ELSE NULL 
                        END
     , HND_EMPNO = #{hndEmpno}
     , HND_DETL_DTM = SYSDATE
 WHERE 1=1
   AND DECD_SN = #{decdSn}
