mapper.select:
SELECT DECD_SQ
  FROM IBIMS232B
 WHERE 1=1
   AND DECD_SN = #{decdSn}
   AND DCFC_ENO = #{dcfcEno}

mapper.insert:
INSERT INTO IBIMS232B (
	  DECD_SN
	, DECD_SQ
	, DECD_STTS_DCD
	, DCFC_ENO
	, DECD_DTM
	, DCFC_ANNO_CNTN
	, RJCT_YN
	, RJCT_RSN_CNTN
	, HND_DETL_DTM
	, HND_EMPNO
	, HND_TMNL_NO
	, HND_TR_ID
	, GUID
) VALUES (
	  #{decdSn}
	, #{decdSq}
	, #{decdSttsDcd}
	, #{dcfcEno}
	, #{decdDtm}
	, #{dcfcAnnoCntn}
	, 'N'
	, #{rjctRsnCntn}
	, SYSDATE
	, #{hndEmpno}
	, #{hndTmnlNo}
	, #{hndTrId}
	, #{guid}
)

mapper.update:
      UPDATE IBIMS232B
         SET DECD_STTS_DCD = #{decdSttsDcd}
		   , DECD_DTM = SYSDATE

           , DCFC_ANNO_CNTN = #{dcfcAnnoCntn}


           , RJCT_YN = #{rjctYn}


		   , RJCT_RSN_CNTN = #{rjctRsnCntn}

           , HND_EMPNO = #{hndEmpno}
           , HND_DETL_DTM = SYSDATE
       WHERE 1=1
         AND DECD_SN = #{decdSn}

		 AND DECD_SQ = #{decdSq}
