mapper.select:
SELECT T.DECD_SN /* 결재일련번호 */ , T.CHRR_ENO /* 책임자사번 */ , T.CHRR_ENM /* 책임자명 */ , T.APVL_RQST_P_ENO /* 승인요청자사번 */ , T.APVL_RQST_P_ENM /* 승인요청자명 */ , T.DECD_STEP_DCD /* 결재단계구분코드 */ , T.DECD_STEP_NM /* 결재단계명 */ , T.DECD_STTS_DCD /* 결재상태구분코드 */ , T.DECD_STTS_NM /* 결재상태명 */ , T.PRDT_CD /* 종목코드 */ , ( SELECT MAX(PRDT_NM) FROM IBIMS201B WHERE 1=1 AND PRDT_CD = T.PRDT_CD ) AS PRDT_NM /* 종목명 */ , T.DEAL_NO /* 딜번호 */ , T.DEAL_NM /* 딜명 */ , T.DECD_JOB_DCD /* 결재업무구분코드 */ , T.DECD_JOB_NM /* 결재업무명 */ , T.SCRN_NO /* 화면번호 */ , T.APVL_RQST_CNTN /* 승인요청내용 */ , T.RQST_DTM /* 신청일시 */ , T.RQST_CNCL_DTM /* 신청취소일시 */ , T.DCFC_ENO /* 담당자사번 */ , T.DCFC_ENM /* 담당자명 */ , T.PRCS_RSLT_DCD /* 처리결과구분코드 */ , T.PRCS_RSLT_NM /* 처리결과명 */ , T.EXC_SEQ /* 실행순번 */ , T.RQST_SQ /* 신청순번 */ , T.TR_SEQ /* 거래순번 */ , T.ERR_CNTN /* 오류내번 */ , T.LAST_DECD_SQ /* 최종결재순번 */ FROM ( SELECT A.DECD_SN , A.CHRR_ENO , (SELECT EMP_NM FROM IBIMS003B WHERE EMPNO = A.CHRR_ENO) AS CHRR_ENM , A.APVL_RQST_P_ENO , (SELECT EMP_NM FROM IBIMS003B WHERE EMPNO = A.APVL_RQST_P_ENO) AS APVL_RQST_P_ENM , A.DECD_STEP_DCD , (SELECT D.CD_VL_NM FROM IBIMS001B A RIGHT JOIN IBIMS002B D ON A.CMNS_CD_GRP = D.CMNS_CD_GRP WHERE A.CMNS_CD_GRP = 'D016' AND D.CD_VL_ID = A.DECD_STEP_DCD) AS DECD_STEP_NM , B.DECD_STTS_DCD , (SELECT D.CD_VL_NM FROM IBIMS001B A RIGHT JOIN IBIMS002B D ON A.CMNS_CD_GRP = D.CMNS_CD_GRP WHERE A.CMNS_CD_GRP = 'D006' AND D.CD_VL_ID = B.DECD_STTS_DCD) AS DECD_STTS_NM , A.PRDT_CD , A.DEAL_NO , C.DEAL_NM , A.DECD_JOB_DCD , (SELECT D.CD_VL_NM FROM IBIMS001B A RIGHT JOIN IBIMS002B D ON A.CMNS_CD_GRP = D.CMNS_CD_GRP WHERE A.CMNS_CD_GRP = 'D005' AND D.CD_VL_ID = A.DECD_JOB_DCD) AS DECD_JOB_NM , A.SCRN_NO , A.APVL_RQST_CNTN , TO_CHAR(A.RQST_DTM, 'YYYYMMDDHH24MISS') AS RQST_DTM , TO_CHAR(A.RQST_CNCL_DTM, 'YYYYMMDDHH24MISS') AS RQST_CNCL_DTM , B.DCFC_ENO , (SELECT EMP_NM FROM IBIMS003B WHERE EMPNO = B.DCFC_ENO) AS DCFC_ENM , A.PRCS_RSLT_DCD , (SELECT D.CD_VL_NM FROM IBIMS001B A RIGHT JOIN IBIMS002B D ON A.CMNS_CD_GRP = D.CMNS_CD_GRP WHERE A.CMNS_CD_GRP = 'P028' AND D.CD_VL_ID = A.PRCS_RSLT_DCD) AS PRCS_RSLT_NM , A.EXC_SEQ , A.RQST_SQ , A.TR_SEQ , A.ERR_CNTN , A.LAST_DECD_SQ FROM IBIMS231B A /* 딜승인결재기본 */ , IBIMS232B B /* 딜승인결재담당자내역 */ , (SELECT DEAL_NO, DEAL_NM FROM IBIMS101B GROUP BY DEAL_NO, DEAL_NM) C /* 딜기본정보 */ WHERE A.DECD_SN = B.DECD_SN AND A.DEAL_NO = C.DEAL_NO
AND A.CHRR_ENO = #{chrrEno}
AND A.APVL_RQST_P_ENO = #{apvlRqstPEno}
AND A.DECD_STEP_DCD = #{decdStepDcd}
) T

SELECT DECD_SQ
  FROM IBIMS232B
 WHERE 1=1
   AND DECD_SN = #{decdSn}
   AND DCFC_ENO = #{dcfcEno}

mapper.insert:
INSERT INTO IBIMS232B (
	  DECD_SN
	, DECD_SQ
	, DECD_STTS_DCD
	, DCFC_ENO
	, DECD_DTM
	, DCFC_ANNO_CNTN
	, RJCT_YN
	, RJCT_RSN_CNTN
	, HND_DETL_DTM
	, HND_EMPNO
	, HND_TMNL_NO
	, HND_TR_ID
	, GUID
) VALUES (
	  #{decdSn}
	, #{decdSq}
	, #{decdSttsDcd}
	, #{dcfcEno}
	, #{decdDtm}
	, #{dcfcAnnoCntn}
	, 'N'
	, #{rjctRsnCntn}
	, SYSDATE
	, #{hndEmpno}
	, #{hndTmnlNo}
	, #{hndTrId}
	, #{guid}
)

mapper.update:
      UPDATE IBIMS232B
         SET DECD_STTS_DCD = #{decdSttsDcd}
		   , DECD_DTM = SYSDATE

           , DCFC_ANNO_CNTN = #{dcfcAnnoCntn}


           , RJCT_YN = #{rjctYn}


		   , RJCT_RSN_CNTN = #{rjctRsnCntn}

           , HND_EMPNO = #{hndEmpno}
           , HND_DETL_DTM = SYSDATE
       WHERE 1=1
         AND DECD_SN = #{decdSn}

		 AND DECD_SQ = #{decdSq}
