mapper.select:
SELECT COUNT(STTM_DETL_SN)
  FROM IBIMS432B
 WHERE 1=1
   AND WRTN_DT = #{wrtnDt}
   AND RSLN_BDCD = #{rslnBdcd}
   AND CNST_NO = #{cnstNo}

mapper.insert:
INSERT INTO IBIMS432B
    (
        WRTN_DT
      , RSLN_BDCD
      , CNST_NO
      , STTM_DETL_SN
      , DBIT_CRIT_DCD
      , RPTS_ACTS_CD
      , ACTS_CD
      , KRW_AMT
      , FRCR_AMT
      , BDG_EXCU_BDCD
      , BDG_ACTS_CD
      , RVRS_BDCD
      , RSLN_SYNS_CTNS
      , FNDS_ISTR_JOB_CLSF_CD
      , ACCT_BCNC_CD
      , PRUF_KND_DCD
      , PRUF_DT
      , NTS_APVL_NO
      , ELC_PRUF_YN
      , VHCL_RGST_CD
      , NS_FND_CD
      , PRDT_CD
      , PROJ_ID
      , CRRY_CD
      , EXRT
      , HND_DETL_DTM
      , HND_EMPNO
      , HND_TMNL_NO
      , HND_TR_ID
      , GUID
    )
VALUES
    (
        #{wrtnDt}
      , #{rslnBdcd}
      , #{cnstNo}
      , #{sttmDetlSn}
      , #{dbitCritDcd}
      , #{rptsActsCd}
      , #{actsCd}
      , NVL(#{krwAmt}, 0)
      , NVL(#{frcrAmt}, 0)
      , #{bdgExcuBdcd}
      , #{bdgActsCd}
      , #{rvrsBdcd}
      , #{rslnSynsCtns}
      , #{fndsIstrJobClsfCd}
      , #{acctBcncCd}
      , #{prufKndDcd}
      , #{prufDt}
      , #{ntsApvlNo}
      , NVL(#{elcPrufYn}, 'N')
      , #{vhclRgstCd}
      , #{nsFndCd}
      , #{prdtCd}
      , #{projId}
      , #{crryCd}
      , #{exrt}
      , SYSDATE
      , #{hndEmpno}
      , #{hndTmnlNo}
      , #{hndTrId}
      , #{guid}
    )

mapper.update:
