 WHERE 1=1
   AND WRTN_DT = #{wrtnDt}
   AND RSLN_BDCD = #{rslnBdcd}
   AND CNST_NO = #{cnstNo}
   AND STTM_DETL_SN = #{sttmDetlSn}

mapper.delete:
DELETE
  FROM IBIMS432B
 WHERE 1=1
   AND WRTN_DT = #{wrtnDt}
   AND RSLN_BDCD = #{rslnBdcd}
   AND CNST_NO = #{cnstNo}
