mapper.select:
	/* IBIMS435BMapper.getRgstSeq */
  SELECT NVL(MAX(RGST_SEQ) + 1, 1)
    FROM IBIMS435B /* 입금내역 */
   WHERE 1=1
     AND RCTM_DT = #{rctmDt}

	/* IBIMS435BMapper.inqPmntPrarAmt */
  SELECT NVL(PMNT_PRAR_AMT, 0)        /* 납부예정금액 */
    FROM IBIMS435B /* 입금내역 */
   WHERE 1=1
     AND RCTM_DT = #{rctmDt} 
     AND RGST_SEQ = #{rgstSeq}

mapper.insert:
	/* IBIMS435BMapper.insertIBIMS435B */
  INSERT INTO IBIMS435B /* 입금내역 */ (
      RCTM_DT
    , RGST_SEQ
    , RGST_BDCD
    , FNDS_DVSN_CD
    , PMNT_PRAR_AMT
    , DEAL_RCTM_AMT
    , RELT_ISTT_CD
    , RELT_ISTT_NM
    , RELT_BANO
    , DPTR_NM
    , HND_DETL_DTM
    , HND_EMPNO
    , HND_TMNL_NO
    , HND_TR_ID
    , GUID
  )
  VALUES(
      #{rctmDt}
    , #{rgstSeq}
    , #{rgstBdcd}
    , #{fndsDvsnCd}
    , #{pmntPrarAmt}
    , #{dealRctmAmt}
    , #{reltIsttCd}
    , #{reltIsttNm}
    , #{reltBano}
    , #{dptrNm}
    , SYSDATE
    , #{hndEmpno}
    , #{hndTmnlNo}
    , #{hndTrId}
    , #{guid}
  )

mapper.update:
	/* IBIMS435BMapper.updateIBIMS435B */
  UPDATE IBIMS435B /* 입금내역 */
     SET HND_DETL_DTM = SYSDATE
       , HND_EMPNO = #{hndEmpno}
       , HND_TMNL_NO = #{hndTmnlNo}
       , HND_TR_ID = #{hndTrId}
       , GUID = #{guid}

       , FNDS_DVSN_CD = #{fndsDvsnCd}


       , RELT_ISTT_CD = #{reltIsttCd}


       , RELT_ISTT_NM = #{reltIsttNm}


       , RELT_BANO = #{reltBano}


       , DPTR_NM = #{dptrNm}

   WHERE 1=1
     AND RCTM_DT = #{rctmDt} 
     AND RGST_SEQ = #{rgstSeq}

	/* IBIMS435BMapper.updatePmntPrarAmt */
  UPDATE IBIMS435B /* 입금내역 */
     SET PMNT_PRAR_AMT = #{pmntPrarAmt}
   WHERE 1=1
     AND RCTM_DT = #{rctmDt}
     AND RGST_SEQ = #{rgstSeq}

mapper.delete:
	/* IBIMS435BMapper.deleteIBIMS435B */
  DELETE 
    FROM IBIMS435B /* 입금내역 */
   WHERE 1=1
     AND RCTM_DT = #{rctmDt} 
     AND RGST_SEQ = #{rgstSeq}
