mapper.select:
SELECT A.DCSN_YN /* 확정여부 */ , A.AFCT_MNGM_NO /* 사후관리번호 */ , A.DEAL_NO /* 딜번호 */ , B.DEAL_NM /* 딜명 */ , A.PRDT_CD /*종목코드*/ , A.EXC_SN /* 실행순번 */ , A.OVDU_STTS_CD /* 연체상태코드 */ , CASE A.OVDU_STTS_CD WHEN '0' THEN '정상' WHEN '1' THEN '일반연체' WHEN '2' THEN '기한이익상실' END AS OVDU_STTS_NM /* 연체상태명 */ , A.CRDL_BLCE_AMT /* 여신잔액금액 */ , A.OVDU_PRNA_AMT /* 연체원금액 */ , A.OVDU_INTR_AMT /* 연체이자금액 */ , A.FRS_PRNA_OVDU_OCRNC_DT /* 최초원금연체발생일 */ , A.FRS_INTR_OVDU_OCRNC_DT /* 최초이자연체발생일 */ , A.PRCA_OVDU_ACML_DNUM /* 원금연체누적일수 */ , A.INT_OVDU_ACML_DNUM /* 이자연체누적일수 */ , A.LAST_PRNA_RDMP_DT /* 최종원금상환일 */ , A.LAST_INTR_RDMP_DT /* 최종이자상환일 */ , A.OVDU_RLSE_DT /* 연체해제일 */ , C.BDCD /* 거래조직번호 */ , C.DPRT_NM /* 조직명 */ , C.DPRT_CD /* 조직코드 */ FROM IBIMS436B A , IBIMS101B B , IBIMS003B C WHERE 1=1 AND C.DEL_YN = 'N'
AND A.DEAL_NO = B.DEAL_NO(+) AND B.LAST_YN(+) = 'Y' AND B.CHRR_EMPNO = C.EMPNO(+)
AND A.PRDT_CD = #{prdtCd}
AND A.EXC_SN = #{excSn}
AND ( A.FRS_PRNA_OVDU_OCRNC_DT >= #{startDt} OR A.FRS_INTR_OVDU_OCRNC_DT >= #{startDt} ) AND ( A.FRS_PRNA_OVDU_OCRNC_DT <= #{endDt} OR A.FRS_INTR_OVDU_OCRNC_DT <= #{endDt} )
AND A.OVDU_RLSE_DT IS NOT NULL
AND A.OVDU_RLSE_DT IS NULL
AND C.DPRT_CD = #{dprtCd}

mapper.update:
UPDATE IBIMS436B
   SET DCSN_YN = #{dcsnYn}
 WHERE 1=1
   AND AFCT_MNGM_NO = #{afctMngmNo}
   AND DEAL_NO = #{dealNo}
   AND EXC_SN = #{excSn}

UPDATE IBIMS436B
    SET OVDU_STTS_CD = '02'
        , OVDU_RLSE_DT = #{ovduRlseDt}
WHERE 1=1
    AND AFCT_MNGM_NO = #{afctMngmNo}
    AND DEAL_NO = #{dealNo}
    AND PRDT_CD = #{prdtCd}
    AND EXC_SN = #{excSn}
