mapper.select:
SELECT DEAL_NO /* Deal번호 */ , STDR_YM /* 기준년월 */ , (SELECT CD_VL_NM FROM IBIMS002B WHERE CMNS_CD_GRP = 'B014' AND CD_VL_ID = BUSI_PRG_STEP) AS BUSI_PRG_STEP_NM /* 사업진행단계 */ , BUSI_PRG_STEP , ESTM_PRGS_RT /* 예상진척률 */ , PFMC_PRGS_RT /* 실적진척률 */ , HND_DETL_DTM /* 조작상세일시 */ , HND_EMPNO /* 조작사원번호 */ , HND_TMNL_NO /* 조작단말기번호 */ , HND_TR_ID /* 조작거래id */ , GUID /* guid */ FROM IBIMS611B /* 월별공사및분양형황 */ WHERE 1=1 AND DEAL_NO = #{dealNo}
AND STDR_YM = #{stdrYm}
ORDER BY STDR_YM DESC
