mapper.select:
SELECT T1.TR_DT AS TR_DT -- 거래일자 , T1.TR_TM AS TR_TM -- 거래시간 , T1.ARDY_BZEP_NO AS ARDY_BZEP_NO , T1.FINC_EXCU_RQS_SN AS FINC_EXCU_RQS_SN , T1.TR_SN AS TR_SN , ( SELECT ENTP_NM FROM IBIMS010B WHERE ARDY_BZEP_NO = T2.ARDY_BZEP_NO) AS SPC_NM -- SPC명 , ( SELECT FNLT_NM FROM IBIMS992B WHERE FNLT_CD = T2.ISTT_CD ) AS ISTT_NM -- 은행명 , T2.ASST_MNGM_ACNO AS ASST_MNGM_ACNO -- 자산관리계좌번호 , DECODE(T1.RNDR_DCD, '1', '입금', '출금') AS RNDR_NM -- 입출금구분 , (SELECT CD_VL_NM FROM IBIMS002B WHERE CMNS_CD_GRP = DECODE(T1.RNDR_DCD, '1', 'S010', 'S011') AND CD_VL_ID = DECODE(T1.RNDR_DCD, '1', T1.SPC_DEP_ITEM_KND_CD, T1.SPC_WDR_ITEM_KND_CD)) AS SPC_ITEM_KND_NM -- 항목 , T1.SYNS_TEXT AS SYNS_TEXT -- 적요 , T1.RNDR_AMT AS RNDR_AMT -- 금액 , T1.RNDR_BLCE AS RNDR_BLCE -- 잔고 , ( SELECT ENTP_NM FROM IBIMS010B WHERE ARDY_BZEP_NO = T1.TR_OTHR_DSCM_NO) AS RCVE_P -- 수령자 , (SELECT CD_VL_NM FROM IBIMS002B WHERE CMNS_CD_GRP = 'D010' AND CD_VL_ID = T2.DPRT_CD) AS DPRT_NM -- 관리부서명 , T2.RM_CTNS AS RM_CTNS -- 비고 , DECODE(T3.DECD_STTS_DCD, '2', 'Y', '') AS ASSN_YN -- 승인여부 , T1.SORT_NO AS SORT_NO -- 정렬순서 FROM IBIMS902B T1 , IBIMS900B T2 , IBIMS231B T3 WHERE 1=1 AND T2.ARDY_BZEP_NO = T1.ARDY_BZEP_NO AND T2.FINC_EXCU_RQS_SN = T1.FINC_EXCU_RQS_SN /* AND LPAD(TO_CHAR(T2.FINC_EXCU_RQS_SN), 5, '0') = T3.DEAL_NO(+) */ AND T2.FINC_EXCU_RQS_SN = T3.DEAL_NO(+) AND T3.DECD_STTS_DCD = '2' -- 임시 승인된것만 조회 AND T3.SCRN_NO = 'TB07200S'
AND T2.FINC_EXCU_RQS_DT BETWEEN #{fromDate} AND #{toDate}
AND T1.TR_DT >= #{fromDate}
AND T1.TR_DT <= #{toDate}
AND T2.ARDY_BZEP_NO = #{ardyBzepNo}
AND T2.ASST_MNGM_ACNO = #{asstMngmAcno}
AND T2.DPRT_CD = #{dprtCd}
ORDER BY T1.TR_DT , T1.FINC_EXCU_RQS_SN , T1.SORT_NO

SELECT NVL(TR_SN, 0) + 1
  FROM IBIMS900B
 WHERE 1=1
   AND TR_DT = #{trDt}
   AND ARDY_BZEP_NO = #{ardyBzepNo}
   AND FINC_EXCU_RQS_SN = #{fincExcuRqsSn}

mapper.insert:
