mapper.update:
UPDATE IBIMS902B
   SET RNDR_BLCE = #{rndrBlce}
   	 , HND_DETL_DTM = SYSDATE
	 , HND_EMPNO = #{hndEmpno}
 WHERE 1=1
   AND ARDY_BZEP_NO = #{ardyBzepNo}
   AND FINC_EXCU_RQS_SN = #{fincExcuRqsSn}
   AND TR_DT = #{trDt}
   AND TR_SN = #{trSn}

mapper.select:
		SELECT TR_DT
			 , ARDY_BZEP_NO
			 , FINC_EXCU_RQS_SN
			 , TR_SN
		 WHERE 1=1
		   AND ARDY_BZEP_NO IN (

				#{item.ardyBzepNo}

		   )
		   AND FINC_EXCU_RQS_SN IN (

				#{item.fincExcuRqsSn}

		   )
		   AND NOT (

              (TR_DT = #{item.trDt} AND TR_SN = #{item.trSn})

      )

mapper.delete:
DELETE IBIMS902B
 WHERE 1=1
   AND ARDY_BZEP_NO = #{ardyBzepNo}
   AND FINC_EXCU_RQS_SN = #{fincExcuRqsSn}
   AND TR_DT = #{trDt}
   AND TR_SN = #{trSn}

mapper.update:
UPDATE IBIMS902B
   SET TR_TM        	= TRIM(REPLACE(#{trTm}, ':', '')) 
     , RNDR_AMT        	= #{rndrAmt}
   	 , HND_DETL_DTM     = SYSDATE
	 , HND_EMPNO        = #{hndEmpno}
 WHERE 1=1
   AND ARDY_BZEP_NO     = #{ardyBzepNo}
   AND FINC_EXCU_RQS_SN = #{fincExcuRqsSn}
   AND TR_DT            = #{trDt}
   AND TR_SN            = #{trSn}

mapper.insert:
MERGE INTO IBIMS902B T1 USING ( SELECT T1.TR_DT , T1.ARDY_BZEP_NO , T1.FINC_EXCU_RQS_SN , T1.TR_SN , SUM(CASE WHEN T1.RNDR_DCD = 1 THEN T1.RNDR_AMT WHEN T1.RNDR_DCD = 2 THEN -T1.RNDR_AMT ELSE 0 END) OVER ( PARTITION BY T1.ARDY_BZEP_NO, T1.FINC_EXCU_RQS_SN ORDER BY T1.TR_DT, T1.TR_TM ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW ) AS RNDR_BLCE , ROW_NUMBER() OVER ( PARTITION BY T1.ARDY_BZEP_NO, T1.FINC_EXCU_RQS_SN ORDER BY T1.TR_DT, T1.TR_TM ) AS SORT_SEQ FROM IBIMS902B T1 , IBIMS900B T2 , IBIMS231B T3 WHERE T2.ARDY_BZEP_NO = T1.ARDY_BZEP_NO AND T2.FINC_EXCU_RQS_SN = T1.FINC_EXCU_RQS_SN AND T2.FINC_EXCU_RQS_SN = T3.DEAL_NO(+) AND T3.DECD_STTS_DCD = '2' -- 임시 승인된것만 조회 AND T3.SCRN_NO = 'TB07200S'
AND T2.ARDY_BZEP_NO = #{ardyBzepNo}
AND T2.DPRT_CD = #{dprtCd}
ORDER BY ARDY_BZEP_NO , FINC_EXCU_RQS_SN , SORT_SEQ ) UPDATE_LIST ON ( T1.TR_DT = UPDATE_LIST.TR_DT AND T1.ARDY_BZEP_NO = UPDATE_LIST.ARDY_BZEP_NO AND T1.FINC_EXCU_RQS_SN = UPDATE_LIST.FINC_EXCU_RQS_SN AND T1.TR_SN = UPDATE_LIST.TR_SN ) WHEN MATCHED THEN UPDATE SET T1.RNDR_BLCE = UPDATE_LIST.RNDR_BLCE , T1.SORT_NO = UPDATE_LIST.SORT_SEQ;


            SELECT NVL(MAX(TR_SN),0)+1  		
              FROM IBIMS902B	
             WHERE ARDY_BZEP_NO = #{ardyBzepNo}
			   AND TR_DT = #{trDt}
			   AND FINC_EXCU_RQS_SN = #{fincExcuRqsSn}				

		INSERT INTO IBIMS902B (
			  TR_DT							/* 거래일자 */
			, ARDY_BZEP_NO					/* 기업체번호 */
			, FINC_EXCU_RQS_SN				/* 자금집행신청일련번호 */
			, TR_SN							/* 거래일련번호 */
			, TR_TM 						/* 거래시간 */
			, RNDR_DCD						/* 입출금구분코드 */
			, SPC_DEP_ITEM_KND_CD			/* SPC입금항목종류코드 */
			, SPC_WDR_ITEM_KND_CD			/* SPC출금항목종류코드 */
			, SYNS_TEXT						/* 적요내용 */
			, TR_OTHR_DSCM_NO				/* 거래상대방기업체번호 */
			, RNDR_AMT						/* 입출금금액 */
			, RNDR_BLCE						/* 입출금잔액 */
			, ISTT_CD						/* 기관코드 */
			, ACNO							/* 계좌번호 */
			, RMRK							/* 비고 */
			, HND_DETL_DTM					/* 조작상세일시 */
			, HND_EMPNO						/* 조작사원번호 */
			, HND_TMNL_NO					/* 조작단말기번호 */
			, HND_TR_ID						/* 조작거래ID */
			, GUID							/* GUID */
		) VALUES (
			  #{trDt}
			, #{ardyBzepNo}
			, #{fincExcuRqsSn}
			, #{trSn}
			, TRIM(SUBSTR(REPLACE(#{trTm}, ':', ''), 0, 4))
			, #{rndrDcd}
			, #{spcDepItemKndCd}
			, #{spcWdrItemKndCd}
			, #{synsText}
			, #{trOthrDscmNo}
			, NVL(#{rndrAmt}, 0)
			, NVL(#{rndrBlce}, 0)
			, #{isttCd}
			, #{acno}
			, #{rmrk}
			, SYSDATE
			, #{hndEmpno}
			, #{hndTmnlNo}
			, #{hndTrId}
			, #{guid}
		)

mapper.update:
UPDATE IBIMS902B
   SET TR_TM = TRIM(SUBSTR(REPLACE(#{trTm}, ':', ''), 0, 4))
	 , SPC_DEP_ITEM_KND_CD = #{spcDepItemKndCd}
	 , SPC_WDR_ITEM_KND_CD = #{spcWdrItemKndCd}
	 , SYNS_TEXT = #{synsText}
	 , TR_OTHR_DSCM_NO = #{trOthrDscmNo}
	 , RNDR_AMT = #{rndrAmt}
	 , ISTT_CD = #{isttCd}
	 , ACNO = #{acno}
	 , RMRK = #{rmrk}
	 , HND_DETL_DTM = SYSDATE
	 , HND_EMPNO = #{hndEmpno}
 WHERE 1=1
   AND ARDY_BZEP_NO = #{ardyBzepNo}
   AND FINC_EXCU_RQS_SN = #{fincExcuRqsSn}
   AND TR_DT = #{trDt}
   AND TR_SN = #{trSn}
